insertion at begining void insertAtBeg(int value) { struct Node *newnode; newnode = (struct Node *)malloc(sizeof(struct Node)); newnode->data = value; if (head == NULL) { newnode->next = NULL; head = newnode; } else { newnode->next = head; head = newnode; } printf("

*Inserted successfully at beginning*
"); }

insertion at last void insertATend(int value) { struct Node *newnode; newnode = (struct Node *)malloc(sizeof(struct Node)); newnode->data = value; if (head == NULL) { newnode->next = NULL; head = newnode; } else { struct Node *temp; temp = head; while (temp->next != NULL) { temp = temp->next; } temp->next = newnode; newnode->next = NULL; } printf("

*Inserted Successfully in END *
"); }
